class KeyPressing {
  static keys = new Set();

  static init() {
    window.addEventListener('keydown', e => {
      KeyPressing.keys.add(e.code);
      KeyPressing.keys.add(e.key.toLowerCase());
    });

    window.addEventListener('keyup', e => {
      KeyPressing.keys.delete(e.code);
      KeyPressing.keys.delete(e.key.toLowerCase());
    });

    window.addEventListener('blur', () => {
      KeyPressing.keys.clear();
    });
  }

  static isKeyPressed(key) {
    return KeyPressing.keys.has(key.toLowerCase());
  }
}

KeyPressing.init();
